_isocc2c = {}
for k,v in list(_c2isocc.items()):
	v = v.upper()
	_isocc2c[v] = k
	del _c2isocc[k]
	_c2isocc[k.upper()] = v
del k, v

def iso2country(cc):
	'''map an iso code to a country'''
	return _isocc2c[cc.upper()]

def country2iso(c):
	'''map a country to an iso code'''
	return _c2isocc[c.upper()]

def ccc2sym(ccc):
	return _cccmap[ccc.upper()][1]
